import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { fetchForums } from '../services/api';

const ForumList = () => {
    const [forums, setForums] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const loadForums = async () => {
            try {
                const data = await fetchForums();
                setForums(data);
            } catch (err) {
                console.error('Fehler beim Laden der Foren:', err);
                setError('Fehler beim Laden der Foren.');
            } finally {
                setLoading(false);
            }
        };
        loadForums();
    }, []);

    if (loading) return <p>Laden...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;

    return (
        <div>
            <h1>Forenliste</h1>
            <ul>
                {forums.map((forum) => (
                    <li key={forum.id}>
                        {/* Link zur ForumDetail-Seite */}
                        <Link to={`/forums/${forum.id}`}>{forum.name}</Link>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ForumList;
