import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchPosts } from '../services/api';

const PostList = () => {
    const { forumId } = useParams();
    const [posts, setPosts] = useState([]);
    const [forumDetails, setForumDetails] = useState(null);

    useEffect(() => {
        const getPosts = async () => {
            try {
                const postsData = await fetchPosts(forumId);
                setPosts(postsData.posts); // Assuming API response has a `posts` field
                setForumDetails(postsData.forum); // Assuming API provides forum details
            } catch (error) {
                console.error('Error fetching posts:', error);
            }
        };
        getPosts();
    }, [forumId]);

    return (
        <div>
            {forumDetails && (
                <div>
                    <h1>{forumDetails.title}</h1>
                    <p>{forumDetails.description}</p>
                    <small>Created by: {forumDetails.createdBy}</small>
                </div>
            )}
            <h2>Posts</h2>
            {posts.length > 0 ? (
                <ul>
                    {posts.map((post) => (
                        <li key={post.id}>
                            <p>{post.content}</p>
                            <small>Posted by: {post.user.username}</small>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No posts available</p>
            )}
        </div>
    );
};

export default PostList;
