// src/components/ForumList.js
import React, { useEffect, useState } from 'react';
import API from '../api';

const ForumList = () => {
    const [forums, setForums] = useState([]);

    useEffect(() => {
        const fetchForums = async () => {
            try {
                const { data } = await API.get('/forums');
                setForums(data);
            } catch (error) {
                alert('Failed to fetch forums');
            }
        };
        fetchForums();
    }, []);

    return (
        <div>
            <h2>Forums</h2>
            <ul>
                {forums.map((forum) => (
                    <li key={forum.id}>{forum.title}</li>
                ))}
            </ul>
        </div>
    );
};

export default ForumList;
