import React from 'react';
import { useParams } from 'react-router-dom';
import CommentSection from '../components/CommentSection';

const PostList = () => {
    const { forumId } = useParams(); // Forum-ID aus der URL abrufen

    return (
        <div>
            <h1>Beiträge im Forum</h1>
            {/* Beiträge im Forum anzeigen */}
            <div>
                {/* Beispielcode für Beiträge */}
                <p>Hier könnten die Beiträge des Forums angezeigt werden.</p>
            </div>
            {/* Kommentarfunktion */}
            <CommentSection forumId={forumId} />
        </div>
    );
};

export default PostList;
