import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { fetchForums, createForum } from '../services/api';

const ForumList = () => {
    const [forums, setForums] = useState([]);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');

    useEffect(() => {
        const loadForums = async () => {
            const data = await fetchForums();
            setForums(data);
        };
        loadForums();
    }, []);

    const handleCreateForum = async () => {
        if (title && description) {
            await createForum({ title, description });
            const data = await fetchForums();
            setForums(data);
            setTitle('');
            setDescription('');
        }
    };

    return (
        <div>
            <h1>Forums</h1>
            <div>
                <input
                    type="text"
                    placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
                <button onClick={handleCreateForum}>Create Forum</button>
            </div>
            <ul>
                {forums.map((forum) => (
                    <li key={forum.id}>
                        <Link to={`/forums/${forum.id}/posts`}>{forum.title}</Link>
                        <p>{forum.description}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ForumList;
