import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchForums } from '../services/api';

const ForumList = () => {
    const [forums, setForums] = useState([]);
    const [error, setError] = useState(null);
    const navigate = useNavigate(); // Hook for navigation

    useEffect(() => {
        const getForums = async () => {
            try {
                const forumsData = await fetchForums();
                setForums(forumsData);
            } catch (err) {
                console.error('Error fetching forums:', err);
                setError('Unable to fetch forums.');
            }
        };
        getForums();
    }, []);

    const handleForumClick = (forumId) => {
        navigate(`/posts/${forumId}`); // Navigate to the PostList page with the forumId
    };

    if (error) {
        return <p>{error}</p>;
    }

    return (
        <div>
            <h1>Forum List</h1>
            {forums && forums.length > 0 ? (
                <ul>
                    {forums.map((forum) => (
                        <li key={forum.id} onClick={() => handleForumClick(forum.id)}>
                            <h3>{forum.title}</h3>
                            <p>{forum.description}</p>
                            <small>Created by: {forum.createdBy}</small>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No forums available</p>
            )}
        </div>
    );
};

export default ForumList;
