export const fetchPostsByForum = async (forumId) => {
    const response = await fetch(`${BASE_URL}/posts?forumId=${forumId}`);
    if (!response.ok) {
        throw new Error('Failed to fetch posts for the forum');
    }
    return response.json();
};
