import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchForums } from '../services/api';

const ForumList = () => {
    const [forums, setForums] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchAllForums = async () => {
            try {
                const forumsData = await fetchForums();
                setForums(forumsData);
            } catch (error) {
                console.error('Error fetching forums:', error);
            }
        };
        fetchAllForums();
    }, []);

    const handleForumClick = (forumId) => {
        navigate(`/posts/${forumId}`); // Navigate to PostList with forumId
    };

    return (
        <div>
            <h1>Forum List</h1>
            {forums.length > 0 ? (
                <ul>
                    {forums.map((forum) => (
                        <li key={forum.id} onClick={() => handleForumClick(forum.id)}>
                            <h3>{forum.title}</h3>
                            <p>{forum.description}</p>
                            <small>Created by: {forum.createdBy}</small>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No forums available</p>
            )}
        </div>
    );
};

export default ForumList;
