import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { fetchForums } from '../services/api'; // Import the correct API method

const ForumList = () => {
    const [forums, setForums] = useState([]);

    useEffect(() => {
        // Fetch forums from the backend
        const fetchAllForums = async () => {
            try {
                const data = await fetchForums(); // Fetch all forums
                setForums(data);
            } catch (error) {
                console.error('Error fetching forums:', error);
            }
        };

        fetchAllForums();
    }, []);

    return (
        <div>
            <h1>Forum List</h1>
            {forums.length > 0 ? (
                <ul>
                    {forums.map((forum) => (
                        <li key={forum.id}>
                            <Link to={`/posts/${forum.id}`}>
                                <h3>{forum.title}</h3>
                            </Link>
                            <p>{forum.description}</p>
                            <small>Created by: {forum.createdBy}</small>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No forums available</p>
            )}
        </div>
    );
};

export default ForumList;
