import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchPosts } from '../services/api';

const PostList = () => {
    const { forumId } = useParams(); // Get forumId from the URL
    const [posts, setPosts] = useState([]);
    const [forumDetails, setForumDetails] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const getPosts = async () => {
            try {
                const postsData = await fetchPosts(forumId); // Fetch posts for the forum
                if (postsData) {
                    setPosts(postsData.posts || []); // Safely set posts (ensure it's an array)
                    setForumDetails(postsData.forum || null); // Set forum details
                }
            } catch (err) {
                console.error('Error fetching posts:', err);
                setError('Unable to fetch posts.');
            }
        };
        getPosts();
    }, [forumId]);

    if (error) {
        return <p>{error}</p>;
    }

    return (
        <div>
            {forumDetails && (
                <div>
                    <h1>{forumDetails.title}</h1>
                    <p>{forumDetails.description}</p>
                    <small>Created by: {forumDetails.createdBy}</small>
                </div>
            )}
            <h2>Posts</h2>
            {posts && posts.length > 0 ? (
                <ul>
                    {posts.map((post) => (
                        <li key={post.id}>
                            <p>{post.content}</p>
                            <small>Posted by: {post.user.username}</small>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No posts available for this forum.</p>
            )}
        </div>
    );
};

export default PostList;
