import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchPostsByForum } from '../services/api';

const PostList = () => {
    const { forumId } = useParams(); // Get forumId from route
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const data = await fetchPostsByForum(forumId); // Fetch posts for this forum
                setPosts(data);
            } catch (error) {
                console.error('Error fetching posts:', error);
            }
        };

        fetchPosts();
    }, [forumId]);

    return (
        <div>
            <h1>Posts for Forum {forumId}</h1>
            {posts.length > 0 ? (
                <ul>
                    {posts.map((post) => (
                        <li key={post.id}>
                            <p>{post.content}</p>
                            <small>By: {post.user.username}</small>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No posts available for this forum.</p>
            )}
        </div>
    );
};

export default PostList;
