import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createForum } from '../services/api';

const CreateForum = () => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const navigate = useNavigate();

    const handleCreateForum = async (e) => {
        e.preventDefault(); // Prevent default form submission
        try {
            await createForum({ title, description }); // Call the API function
            setTitle(''); // Clear the input fields
            setDescription('');
            navigate('/forums'); // Redirect to the forum list
        } catch (error) {
            console.error('Error creating forum:', error); // Log errors for debugging
            alert('Failed to create forum. Please try again.'); // Notify the user
        }
    };

    return (
        <div>
            <h1>Create Forum</h1>
            <form onSubmit={handleCreateForum}>
                <input
                    type="text"
                    placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
                <textarea
                    placeholder="Description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    required
                ></textarea>
                <button type="submit">Create Forum</button>
            </form>
        </div>
    );
};

export default CreateForum;
