import React, { useState, useEffect } from 'react';
import { fetchCommentsByForum, addCommentToForum } from '../services/api';

const CommentSection = ({ forumId }) => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [error, setError] = useState(null);

    useEffect(() => {
        const loadComments = async () => {
            try {
                const data = await fetchCommentsByForum(forumId);
                setComments(data);
            } catch (err) {
                setError('Error loading comments');
            }
        };

        loadComments();
    }, [forumId]);

    const handleAddComment = async () => {
        if (!newComment.trim()) return;

        try {
            const comment = { content: newComment };
            await addCommentToForum(forumId, comment);
            setComments((prev) => [...prev, { content: newComment }]);
            setNewComment('');
        } catch (err) {
            setError('Error adding comment');
        }
    };

    return (
        <div>
            <h2>Comments</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <ul>
                {comments.map((comment, index) => (
                    <li key={index}>{comment.content}</li>
                ))}
            </ul>
            <textarea
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
                placeholder="Add a comment..."
            ></textarea>
            <button onClick={handleAddComment}>Submit</button>
        </div>
    );
};

export default CommentSection;
